import React, { createContext, useState, useEffect } from "react";
import request from "request";

const PriceChangeContext = createContext();

const PriceChangeProvider = (props) => {
  const [price, setPrice] = useState(0);

  useEffect(() => {
    const fetchData = async () => {
      const URL = "https://api.binance.com/api/v3/ticker/24hr?symbol=BTCUSDT";
      const response = await request(URL);
      const data = JSON.parse(response.body);
      setPrice(data.priceChange);
    };
    fetchData();
  }, []);

  return (
    <PriceChangeContext.Provider value={price}>
      {props.children}
    </PriceChangeContext.Provider>
  );
};

export { PriceChangeContext, PriceChangeProvider };
