import React, { useState, useEffect } from 'react';
import './App.css'

const images = [
  'imagem1.jpg',
  'imagem2.jpg',
  'imagem3.jpg',
  // Adicione mais imagens aqui se desejar
];

function App() {
  const [currentImage, setCurrentImage] = useState(0);

  useEffect(() => {
    setInterval(() => {
      setCurrentImage((currentImage + 1) % images.length);
    }, 10000);
  }, [currentImage]);

  return (
    <div>
      <img src={images[currentImage]} alt="Imagem" />
    </div>
  );
}

export default App;
