import React, { useContext, useState, useEffect } from 'react';
import { CoinsPriceContext, CoinsPriceProvider } from './AuthContext.js';
import './App.css'
import btc from './images/btc.png'
import eth from './images/eth.png'
import bnb from './images/bnb.png'
import ada from './images/ada.png'
import xrp from './images/xrp.png'

const images = [
  btc,
  eth,
  bnb,
  ada,
  xrp,
];

function App() {
  const [currentImage, setCurrentImage] = useState(0);
  const CoinsPriceProvider = useContext(CoinsPriceContext);

  useEffect(() => {
    setInterval(() => {
      setCurrentImage((currentImage + 1) % images.length);
    }, 10000);
  }, [currentImage]);

  return (
    <section>
      <div className='container'>
        <h1>Crypto Tracker</h1>
      </div>
      <div className='containerleft'>
        <h1>Ranking</h1>
        <h2>Name</h2>
        <h2>Price</h2>
        <h2>1d%</h2>
      </div>
      <div>
        <h1> {CoinsPriceProvider.btcPrice}</h1>
      </div>
      <div className='containerright'>
        <img src={images[currentImage]} alt="Imagem" />
      </div>
    </section>
  );
}

export default () => (
  <CoinsPriceProvider>
    <App />
  </CoinsPriceProvider>
);
